package leetcode.alice.string;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

class FindAllAnagramsInString {
	public static void main(String[] args) {
		//findAnagrams("abab", "ab");
		findAnagrams(""
		,"yqrbgjdwtcaxzsnifvhmou");
	}
    public static List<Integer> findAnagrams(String s, String p) {
        int pSize = p.length();
        char[] pChar = p.toCharArray();
        Arrays.sort(pChar);
        List<Integer> result = new ArrayList<Integer>();
        for(int i=0; i<s.length(); i++){
            if(p.indexOf(s.charAt(i)) >= 0 && i+pSize <= s.length()){
                String sub = s.substring(i, i+pSize);
                char[] sChar = sub.toCharArray();
                Arrays.sort(sChar);
                if(Arrays.equals(sChar,pChar)){
                    result.add(i);
                }
            }
        }
        
        return result;
    }
}